import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API_BASE = 'http://127.0.0.1:5000/api';

function App() {
  const [target, setTarget] = useState('');
  const [loading, setLoading] = useState(false);
  const [result, setResult] = useState(null);
  const [history, setHistory] = useState([]);
  const [stats, setStats] = useState({});
  const [currentTime, setCurrentTime] = useState(new Date());

  useEffect(() => {
    loadHistory();
    loadStats();
    const timer = setInterval(() => setCurrentTime(new Date()), 1000);
    return () => clearInterval(timer);
  }, []);

  const loadHistory = async () => {
    try {
      const response = await axios.get(`${API_BASE}/history`);
      setHistory(response.data);
    } catch (error) {
      console.error('Error loading history:', error);
    }
  };

  const loadStats = async () => {
    try {
      const response = await axios.get(`${API_BASE}/stats`);
      setStats(response.data);
    } catch (error) {
      console.error('Error loading stats:', error);
    }
  };

  const handleScan = async (e) => {
    e.preventDefault();
    if (!target.trim()) return;

    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE}/scan`, { target });
      setResult(response.data);
      loadHistory();
      loadStats();
    } catch (error) {
      console.error('Scan error:', error);
      setResult({ error: 'Scan failed. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  const getThreatLevel = (vtResult) => {
    if (!vtResult || vtResult.error) return 'unknown';
    const positives = vtResult.positives || 0;
    if (positives === 0) return 'clean';
    if (positives <= 2) return 'low';
    if (positives <= 5) return 'medium';
    return 'high';
  };

  const getThreatColor = (level) => {
    switch (level) {
      case 'clean': return '#10B981';
      case 'low': return '#F59E0B';
      case 'medium': return '#F97316';
      case 'high': return '#EF4444';
      default: return '#6B7280';
    }
  };

  const getThreatGradient = (level) => {
    switch (level) {
      case 'clean': return 'linear-gradient(135deg, #10B981, #059669)';
      case 'low': return 'linear-gradient(135deg, #F59E0B, #D97706)';
      case 'medium': return 'linear-gradient(135deg, #F97316, #EA580C)';
      case 'high': return 'linear-gradient(135deg, #EF4444, #DC2626)';
      default: return 'linear-gradient(135deg, #6B7280, #4B5563)';
    }
  };

  const pulseAnimation = {
    animation: 'pulse 2s infinite',
  };

  const fadeInAnimation = {
    animation: 'fadeIn 0.5s ease-in',
  };

  const styles = `
    @keyframes pulse {
      0%, 100% { opacity: 1; }
      50% { opacity: 0.5; }
    }
    
    @keyframes fadeIn {
      from { opacity: 0; transform: translateY(20px); }
      to { opacity: 1; transform: translateY(0); }
    }
    
    @keyframes slideIn {
      from { transform: translateX(-100%); opacity: 0; }
      to { transform: translateX(0); opacity: 1; }
    }
    
    @keyframes glow {
      0%, 100% { box-shadow: 0 0 5px rgba(59, 130, 246, 0.5); }
      50% { box-shadow: 0 0 20px rgba(59, 130, 246, 0.8), 0 0 30px rgba(59, 130, 246, 0.6); }
    }
    
    .card-hover {
      transition: all 0.3s ease;
      cursor: pointer;
    }
    
    .card-hover:hover {
      transform: translateY(-5px);
      box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
    }
    
    .scan-button-hover {
      transition: all 0.3s ease;
      position: relative;
      overflow: hidden;
    }
    
    .scan-button-hover:hover:not(:disabled) {
      transform: translateY(-2px);
      box-shadow: 0 10px 25px rgba(59, 130, 246, 0.4);
    }
    
    .scan-button-hover:before {
      content: '';
      position: absolute;
      top: 0;
      left: -100%;
      width: 100%;
      height: 100%;
      background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
      transition: left 0.5s;
    }
    
    .scan-button-hover:hover:before {
      left: 100%;
    }
  `;

  return (
    <div style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 50%, #667eea 100%)',
      backgroundSize: '400% 400%',
      animation: 'gradient 15s ease infinite',
      fontFamily: "'Segoe UI', 'Roboto', sans-serif",
      position: 'relative',
      overflow: 'hidden'
    }}>
      <style>{styles}</style>
      
      {/* Animated Background Elements */}
      <div style={{
        position: 'absolute',
        top: '10%',
        left: '10%',
        width: '300px',
        height: '300px',
        background: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '50%',
        animation: 'float 6s ease-in-out infinite',
        zIndex: 0
      }}></div>
      
      <div style={{
        position: 'absolute',
        bottom: '20%',
        right: '15%',
        width: '200px',
        height: '200px',
        background: 'rgba(255, 255, 255, 0.1)',
        borderRadius: '50%',
        animation: 'float 8s ease-in-out infinite reverse',
        zIndex: 0
      }}></div>

      <div style={{ 
        position: 'relative',
        zIndex: 1,
        maxWidth: '1400px', 
        margin: '0 auto',
        padding: '20px'
      }}>
        {/* Header Section */}
        <div style={{ 
          textAlign: 'center', 
          marginBottom: '40px',
          color: 'white',
          ...fadeInAnimation
        }}>
          <div style={{
            display: 'inline-flex',
            alignItems: 'center',
            gap: '15px',
            marginBottom: '15px'
          }}>
            <div style={{
              fontSize: '60px',
              animation: 'glow 2s infinite alternate'
